import React from 'react'
import './ToduList.css'
import { useSelector , useDispatch } from 'react-redux';

function ToduList() {
  const select = useSelector((state)=> state.todo )
  const dispatch = useDispatch()
  function isCompleted(item){
    return item.complete === true? "#28a745" : "transparent";
  }
  const deleteTodo = (id)=>{
    dispatch(deleteTodo())
  }
  return (
    <div className='ToduList'>
      <table>
        {select?.map((item)=>{
          return(
            <div className='tableCondainer' key={item.id} style={{backgroundColor:isCompleted(item)}}>
              <tr>
                <td><input type="checkbox" id={item.id} name={item.todo} value={true} /></td>
                <td className='todoData'>{item.todo}</td>
                <td><button onClick={()=>deleteTodo(item.id)}>Delete</button></td>
              </tr>
            </div>)
        })}
      </table>
    </div>
  )
}

export default ToduList
