import React , { useRef, useState } from 'react'
import './ToduList.css'
import { useSelector , useDispatch } from 'react-redux';
import { deleteTodo } from '../redux/todoSlice';

function ToduList() {
  const select = useSelector((state)=> state.todo )
  const dispatch = useDispatch()
  const [isChecked,setIsChecked] = useState(fals)

  function isCompleted(item){
    return isChecked === true? "#28a745" : "transparent";
  }
  const deleteT = (id) => {
    dispatch(deleteTodo(id))
  }
  return (
    <div className='ToduList'>
      <table>
        {select?.map((item)=>{
          return(
            <div className='tableCondainer' key={item.id} style={{backgroundColor:isCompleted(item)}}>
              <tr>
                <td><input type="checkbox" id={item.id} name={item.todo} defaultChecked={isChecked} onChange={()=>{
                  if(isChecked) {setIsChecked(false) }
                  else {setIsChecked(false)}
                }} /></td>
                <td className='todoData'>{item.text}</td>
                <td><button onClick={()=>deleteT(item.id)}>Delete</button></td>
              </tr>
            </div>)
        })}
      </table>
    </div>
  )
}

export default ToduList
